import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'
import Button from '@material-ui/core/Button';
import Axios from 'axios'
import { Form } from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

import Test from './Test'

const useStyles = makeStyles((theme) => ({
    rootBtn: {
      '& > *': {
        margin: theme.spacing(0.1),
      },display: 'flex'
    },
  }));
  
  function ReactScannarA() {
    const [result, setResult] = useState('No result')
    const [values, setValues] = useState('1')
    const [On ,setOn] = useState(false)
    const classes = useStyles();
    
    const UpdatStore = (e) => {
        Axios.put('http://localhost:3001/api/updateStoreAdd', {
          result: result,
          values: values,
        }).then(() => {
          console.log('withdraw')
        }).then(() => {
          setResult('Withdraw OK!')
          //console.log(result)
        })
    }

    function IsScann () {
      if(result === null){
        console.log(result)
        return <p>Part Code : No Finding</p>
      } else {
        //setOn(false);
        return 
        (
        <div>
          <p>Part Code :{result}</p>
          <Grid item={12}>
            <div className={classes.rootBtn}>
                <Grid item={4}>
                    <Form > 
                        <Form.Group>
                            <Form.Control type="number" size="md" placeholder="Values" onChange={(e) => setValues(e.target.value)}/>
                        </Form.Group>
                    </Form>
                </Grid>
                <Grid item={4}>
                    <Button  variant="contained" color="Primary" onClick={UpdatStore}> Withdraw </Button>
                </Grid>
            </div>
          </Grid>
        </div>
        )
        
        setOn(true)
        //setTimeout(fn, 5000)
        //Modal
        
      }
    }

    return (
        <div >
            <QrReader
                delay={500}
                onError={err => console.log(err)}
                legacyMode={On}
                onScan={(data) => setResult(data)}
                style={{ width: '100%', height: '200px' }}/>
            <div styles={{display: 'flex'}}>
                {IsScann(result)}
                
            </div>    
        </div>
    )
}

export default ReactScannarA


/**
 <Grid item={12}>
    <div className={classes.rootBtn}>
        <Grid item={4}>
            <Form > 
                <Form.Group>
                    <Form.Control type="number" size="md" placeholder="Values" onChange={(e) => setValues(e.target.value)}/>
                </Form.Group>
            </Form>
        </Grid>
        <Grid item={4}>
            <Button  variant="contained" color="Primary" onClick={UpdatStore}> Withdraw </Button>
        </Grid>
    </div>
 </Grid>


                <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description">
            {body}
            </Modal>
 */
