import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import { Grid } from '@material-ui/core';
import ReactQrCodeScanner from '../PageDashboard/ReactQrCodeScanner';


const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  rootBtn: {
    '& > *': {
      margin: theme.spacing(0.1),
    },display: 'flex'
  },
}));

export default function SModal() {
  const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [openA, setOpenA] = React.useState(false);
  const [openB, setOpenB] = React.useState(false);


  const handleOpenA = () => {
    setOpenA(true);
  };
  const handleCloseA = () => {
    setOpenA(false);
  };
  const handleOpenB = () => {
    setOpenB(true);
  };
  const handleCloseB = () => {
    setOpenB(false);
  };

  function getModalStyle() {
    const top = 50 
    const left = 50 
  
    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: `translate(-${top}%, -${left}%)`,
    };
  }
  
  const bodyA = (
    <div  className={classes.paper}>
      <ReactQrCodeScanner />
    </div>
  );

  const bodyB = (
    <div style={modalStyle} className={classes.paper}>
      <h2 id="simple-modal-title">Text in a modal</h2>
      <p id="simple-modal-description">
        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
    </div>
  );

  return (
    
        <Grid item xs={12}>
        <div className={classes.rootBtn}>
            <Button fullWidth variant="contained" color="secondary" onClick={handleOpenA}> 
                Withdraw 
            </Button>     
            <Button  fullWidth variant="contained" color="Primary" onClick={handleOpenB}> 
                To Store  
            </Button>
        <Modal
            open={openA}
            onClose={handleCloseA}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
        >
            {bodyA}
        </Modal>

        <Modal
            open={openB}
            onClose={handleCloseB}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
        >
            {bodyB}
        </Modal>
        </div>
      </Grid>
  );
}
