    const UpdatStore = (e) => {
      Axios.post('http://localhost:3001/api/updateStoreAdd', {
        result:result,
      }).then(() => {
        console.log('test')
      })
    }

    const OutStore = () => {
      Axios.post('http://localhost:3001/api/updateStoreOut', {
        result:result,
      }).then(() => {
        console.log('test')
      })
    }

    const handleError = (err) => {
      console.error(err)
    }

    const handleScan = (data) => {
      setResult({
        result: data,
      })
    }

  return (
    <Grid container >
      <Grid className={classes.root} item xs={12}>
        <Grid item xs={8} ></Grid>
        <Grid item xs={4}>
          <div className={classes.root1}>
              <div className={classes.root2}>
              <BarcodeReader
              onError={handleError}
              onScan={handleScan}
              />
              </div>
              <p className={classes.p}>{result}</p>
              
                <Button fullWidth variant="contained" color="Primary" onClick={UpdatStore}>Withdraw</Button>
                

          </div>
        </Grid>
      </Grid>
    </Grid>
  )
}
 
export default ReactWebcamBarcodeScanner;

