import React, { useState } from 'react'
import axios from 'axios'
import { Form, InputGroup, FormControl, Col } from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import html2canvas from 'html2canvas'

import QRCode from "qrcode.react";


const useStyles = makeStyles((theme) => ({
    add: {
        '& > *': {
            padding: '10px',
            marginTop: '24px'},
    },
    Form: {
        marginTop: '16px'
    },
    A :{
        marginTop: '10px'
    },
    C:{
        margin: '20px',
        marginTop: '20px',
        marginBottom: '20px',
        //backgroundColor: 'blue'
    }
}))

function Add(props) {
    const classes = useStyles();

    const [PartCode, setPartCode] = useState("")
    const [Brand, setBrand] = useState("none")
    const [Location_at, setLocation] = useState(null)
    const [Description, setDescription] = useState("none")
    const [Values, setValues] = useState("1")
    // const [img, setImg] = useState(null)
    // const [imgData, setImgdata] = useState(null)
    // const [qrValue, setQrValue] = useState(PartCode);

    

    const handlerSubmit = (e) => {
        if(PartCode == "" && Location_at == null) {
            console.error();
            return alert("Error! => Part Code & Location should be not empty")
        } else {
            axios.post('http://localhost:3001/api/insert', {
            PartCode : PartCode,
            Brand : Brand,
            Location_at : Location_at,
            Values : Values,
            Description : Description,
        }).then(() => {
            download()
            alert("successfull insert")
        }).then(window.location.reload(false))
        }
         const canvas = html2canvas(document.querySelector(`#capture`));
         const pngUrl = canvas.toDataURL("image/png").replace("image/png", "image/octet-stream");
         let downloadLink = document.createElement("a");
         downloadLink.href = pngUrl;
         downloadLink.download = `${PartCode}.png`;
         document.body.appendChild(downloadLink);
         downloadLink.click();
         document.body.removeChild(downloadLink);
    }

    // const download = () => {
    //     html2canvas(document.querySelector(`#capture`)).then(canvas => {
    //         let dataURL = canvas.toDataURL('image/png');
    //         var link = document.createElement('a');
    //         link.download = `${PartCode}.png`;
    //         link.href = dataURL;
    //         link.click();
    //      })
    // }

    // const onChangePicture = e => {
    //     if (e.target.files[0]) {
    //         console.log("picture:", e.target.files)
    //         setImg(e.target.files[0])
    //         const reader = new FileReader();
    //         reader.addEventListener("load", () => {
    //             setImgdata(reader.result)
    //         })
    //         reader.readAsDataURL(e.target.files[0])
    //     }
    // }

    const handleOnChange = event => {
        const { value } = event.target;
        setPartCode(event.target.value.toUpperCase())
      };

    return (
        <Grid container spacing={1}>
            <Grid item xs={12} >
                <div className={classes.A}>
                    <Form>
                        <Form.Group >
                            <Form.Control type="text" size="md" placeholder="Part Code" onChange={handlerSubmit} />
                        </Form.Group>
                        <Form.Group >
                            <Form.Control type="text" size="md" placeholder="Brand" onChange={(e) => setBrand(e.target.value.toUpperCase())}/>
                        </Form.Group>
                        <Form.Row>
                            <Form.Group as={Col}>
                                    <Form.Control type="text" size="md" placeholder="Location" onChange={(e) => setLocation(e.target.value.toUpperCase())}/>
                            </Form.Group>
                            <Form.Group as={Col}>
                                    <Form.Control type="number" size="md" placeholder="Values" onChange={(e) => setValues(e.target.value)}/>
                            </Form.Group>
                        </Form.Row>
                        {/*
                        <Form.Group >
                            <Form.File id="img" custom label="Image" onChange={onChangePicture}/>
                        </Form.Group>
                        */}
                        <InputGroup>
                            <InputGroup.Prepend>
                                <InputGroup.Text>Description</InputGroup.Text>
                            </InputGroup.Prepend>
                            <FormControl as="textarea" size="md" aria-label="With textarea" onChange={(e) => setDescription(e.target.value)}/>
                        </InputGroup>

                        <div id={`capture`} className={classes.C}>
                            
                            <QRCode
                                id="qr-gen"
                                value={PartCode}
                                size={150}
                                level={"H"}
                                includeMargin={true}
                            />
                            <br/>
                            {PartCode}
                            {props.children}
                        </div>

                        <Form.Group className={classes.add}>
                            <Button variant="contained" fullWidth color="Primary" onClick={handlerSubmit}>
                                Register
                            </Button>
                        </Form.Group>
                    </Form>
                </div>
            </Grid>
        </Grid>
    )
}

export default Add
