import React ,{ useState }from 'react';
import Axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import logo from '../../../seagate.png'

import { Form, InputGroup, FormControl, Col, Button } from 'react-bootstrap';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      flexDirection: 'center',
      alignContent: 'center'
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      margin: '20px',
      backgroundColor: 'rgb(211, 241, 171)'
    
    },
    login:{
       
    },
    img:{
        height: '465px',
        width: '500px',
    },
    row:{
        flexDirection: 'row',
        display: 'flex',
    }
  }));

function Login() {
    const classes = useStyles();
    const [Usergid, setUsergid] = useState("")
    const [Userpassword, setUserpassword] = useState("")
    const [Status, setStatus] = useState("")
    
    const login = () => {
        Axios.post("http://localhost:3001/api/login", {
            Usergid: Usergid,
            Userpassword: Userpassword,
        }).then((res) => {
            if (res) {
                setStatus(true)
            } else { 
                setStatus(false)
            }
        })
    }

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                <Grid item xs={12} >
                    <Paper className={classes.paper}>
                        <Paper elevation={3} className={classes.login}>

                        </Paper>
                        <div className={classes.row}>
                            <Grid item xs={12}>
                                <Form>
                                    <Form.Group >
                                        <Form.Control type="text" size="md" placeholder="GID"  onChange={(e) => { setUsergid(e.target.values)}}/>
                                    </Form.Group>
                                    <Form.Group >
                                        <Form.Control type="text" size="md" placeholder="Password"  onChange={(e) => { setUserpassword(e.target.values)}}/>
                                    </Form.Group>
                                </Form>
                            </Grid>
                            <Grid item xs={4}>
                                <Form.Group >
                                        <Button variant="success" size="lg" type='submit' onClick={login} >
                                            <div style={{height: '65px',textAlign: 'center', paddingTop: '18px'}}>
                                                <h4>Login</h4>
                                            </div>
                                        </Button>{' '}
                                </Form.Group>
                            </Grid>
                        </div>
                    </Paper>
                </Grid>
            </Grid>
        </div>
    )
}

export default Login
