import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'
import Button from '@material-ui/core/Button';
import Axios from 'axios'
import { Form } from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    rootBtn: {
      '& > *': {
        margin: theme.spacing(0.1),
      },display: 'flex'
    },
  }));
  
function ReactScannarA() {
    const [result, setResult] = useState('No result')
    const [values, setValues] = useState(500)
    const classes = useStyles();

    const UpdatStore = (e) => {
        Axios.put('http://localhost:3001/api/updateStoreAdd', {
          result: result,
          values: values,
        }).then(() => {
          console.log('withdraw')
        }).then(() => {
          setResult('Withdraw OK!')
          //console.log(result)
        })
    }

    const IsScann = () => {
      const Found = () => {
        on = true
        return (
            <div>
              <div>
                {On? (<>{result}</>) : (<>B</>)}
              </div>
            </div>
        )
      }
      if (result != null) {
        const Tresult = result
        return <>{Found()}</>
      } 
    }


      // const NotFounf = () => {

      // }
      // const Hold = false

      // if(result != null ){ 
      //   const TrueResult = result
      //   //console.log(TrueResult)
      //   return <p>PART CODE : {Hold?(<>A</>):(<>B</>)} </p>
        
      // } else { 
        
      // }
      //     // if(result === null){
      //     //   console.log(result)
      //     //   return <p>PART CODE :  No Finding</p>
      //     // } else {
      //     //   const TrueResult = result
      //     //   //console.log(TrueResult)
      //     //   return <p>PART CODE : {TrueResult} {Found()} </p>
      //     // }
    

    



    return (
        <div >
            <QrReader
                delay={500}
                onError={err => console.log(err)}
                onScan={(data) => setResult(data)}
                style={{ width: '100%', height: '200px' }}/>
            <div styles={{display: 'flex'}}>
                <p> {IsScann(result)} </p>
            </div>
        </div>
    )
}

export default ReactScannarA


/**
 * 
 * 
 * 
 * 
 * 
    function IsScann () {
      //result != T => On =>
      const Found = () => {
        return (
        <div>
          <Grid item={12}>
            <div className={classes.rootBtn}>
                <Grid item={4}>
                    <Form > 
                        <Form.Group>
                            <Form.Control type="number" size="md" placeholder="Values" onChange={(e) => setValues(e.target.value)}/>
                        </Form.Group>
                    </Form>
                </Grid>
                <Grid item={4}>
                    <Button  variant="contained" color="Primary" onClick={UpdatStore}> Withdraw </Button>
                </Grid>
            </div>
          </Grid>
        </div>
      )}
 * 
 * 
 */
