import React ,{ useState }from 'react';
import Axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import logo from '../../../seagate.png'

import { Form, InputGroup, FormControl, Col} from 'react-bootstrap';
import Button from '@material-ui/core/Button';
const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      flexDirection: 'center',
      alignContent: 'center'
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      margin: '20px',
      backgroundColor: '#ffffff'
    
    },
    login:{
       
    },
    img:{
        height: '465px',
        width: '500px',
    },
    row:{
        marginBottom: '50px'
    }
  }));

function Login() {
    const classes = useStyles();
    const [Usergid, setUsergid] = useState("")
    const [Userpassword, setUserpassword] = useState("")
    const [Status, setStatus] = useState("")
    
    const login = () => {
        Axios.post("http://localhost:3001/api/login", {
            Usergid: Usergid,
            Userpassword: Userpassword,
        }).then((res) => {
            if (res) {
                setStatus(true)
            } else { 
                setStatus(false)
            }
        })
    }

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                <Grid item xs={12} >
                    <Paper className={classes.paper} elevation={0}>
                        <div className={classes.row}>
                            <h1>FACILITY STORE</h1>
                            <Grid item xs={12} style={{marginTop: '50px'}}>
                                <Form>
                                    <Form.Group >
                                        <Form.Control type="text" size="md" placeholder="GID"  onChange={(e) => { setUsergid(e.target.values)}}/>
                                    </Form.Group>
                                    <Form.Group >
                                        <Form.Control type="text" size="md" placeholder="Password"  onChange={(e) => { setUserpassword(e.target.values)}}/>
                                    </Form.Group>
                                </Form>
                            </Grid>
                            <Grid item xs={12}>
                                <Form.Group >
                                    <Button variant="contained" fullWidth color="Primary" onClick={login}>
                                        Login
                                    </Button>
                                </Form.Group>
                            </Grid>
                        </div>
                    </Paper>
                </Grid>
            </Grid>
        </div>
    )
}

export default Login
