//import './App.css';
import React,{ useState } from 'react'
import {BrowserRouter as Router,} from "react-router-dom";
import Axios from 'axios'
/////////////////////////////////////////////////////// M-ui
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { Form } from 'react-bootstrap';
import Button from '@material-ui/core/Button';


/////////////////////////////////////////////////////// File
import Add from './Add/Add'
//import user from '../UserStore.json'
//import LoginPaper from './LoginPaper/LoginPaper'
import Search from './Search/Search'
import SModal from './SModal/Modal'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    margin: '10px'
  },
  PaperL: {
    padding: theme.spacing(1),
    height: 1040,
    //textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  PaperR: {
    padding: theme.spacing(1),
    height: 1040 ,
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  Button: {
    '& > *': {
      margin: theme.spacing(1),
      padding: '20px',
      position: 'relative',
      justify: 'space-evenly'
    },
  logout: {
    marginTop: '50px',
  }
  },
  rootBtn: {
    '& > *': {
      margin: theme.spacing(0.1),
    },display: 'flex'
  },
  a: {
    flexGrow: 1,
    flexDirection: 'center',
    alignContent: 'center'
  },
  b: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    margin: '20px',
    backgroundColor: '#ffffff'
  
  },
  c:{
      marginBottom: '50px'
  }
}));

function Dasboard() {
  const classes = useStyles();
  const [Usergid, setUsergid] = useState('')
  const [Userpassword, setUserpassword] = useState('')
  const [Lstate, setLstate] = useState(true)
  const [admin,setAdmin] = useState(false)

  const Users = ['754638']
  const Password = ['@Ptoleme123']

  const login = ()  => {
    if (Usergid == Users.find(gid => gid == Usergid ) && Userpassword == Password.find(password => password == Userpassword)) { // User.filter(() => {})
      setLstate(false)
      setAdmin(false)
    }
    //window.location.reload(false)
    else if (Usergid == 'admin' && Userpassword == 'admin') {
      setLstate(false)
      setAdmin(true)
    } else {
      alert("GID or PASSWORD ERROR !")
    }
  }

  const logout = () => {
    setLstate(true)
  }
  
  return (
    <Router>
      <div className="App">
        <div className={classes.root}>
          <Grid container spacing={2}>
            <Grid item xs={3}>
            <Paper className={classes.PaperL}>
              {Lstate ? (
                <div className={classes.a}>
                <Grid container spacing={2}>
                    <Grid item xs={12} >
                        <Paper className={classes.b} elevation={0}>
                            <div className={classes.c}>
                                <h1>FACILITY STORE</h1>
                                <Grid item xs={12} style={{marginTop: '40px'}}>
                                    <Form >
                                        <Form.Group >
                                            <Form.Control type="text" size="md" placeholder="GID"  onChange={(e) => { setUsergid(e.target.value)}}/>
                                        </Form.Group>
                                        <Form.Group >
                                            <Form.Control type="password" size="md" placeholder="Password"  onChange={(e) => { setUserpassword(e.target.value)}}/>
                                        </Form.Group>
                                    </Form>
                                </Grid>
                                <Grid item xs={12}>
                                    <Form.Group >
                                        <Button variant="contained" fullWidth color="Primary" onClick={login}>
                                            Login
                                        </Button>
                                    </Form.Group>
                                </Grid>
                            </div>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
              ) : (
                <div className={classes.c}>
                  <Grid item xs={12} style={{marginTop: '180px', margin: '90px'}}>
                    <Form.Group >
                        <h1>GID : {Usergid} </h1>
                        <Button variant="contained" fullWidth color="Primary" onClick={logout}>
                            Logout
                        </Button>
                    </Form.Group>
                    <div>
                      {admin ? (<>T</>) : (<></>)}
                    </div>
                  </Grid>
                </div>
            )}
                <Grid spacing={1}>
                  {!Lstate? (
                    <div>
                      <div>
                        <Add />
                      </div>
                      <Grid item xs={12}>
                        <div >
                          <SModal />
                        </div>
                      </Grid>
                    </div>
                  ) : (
                    <div> 
                      <h3>Please Login to facility store</h3>
                      <h6>project studies by U @2020</h6>
                    </div>
                  )}
              </Grid>
            </Paper>
            </Grid>
            <Grid item xs={9}>
              <Paper className={classes.PaperR}>
                <Grid spacing={2}>
                  <Search /> 
                </Grid>
              </Paper>
            </Grid>
            <Grid item xs={12}>
            </Grid>
          </Grid>
        </div>
      </div>
    </Router>
  );
}


export default Dasboard;


