import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'
import Button from '@material-ui/core/Button';
import Axios from 'axios'
import { Form } from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    container:{
        display:'flex',
        justifyContent:'space-start'
    },
    form:{
        height:'50vh',
        margin: '8px',
        padding:'12px'
    },
    capQr:{
        width:'1vh',
        margin: '8px'
    },
    add: {
        '& > *': {
            padding: '10px',
            marginTop: '24px'},
    },
    Form: {
        marginTop: '16px'
    },
    A :{
        margin: '20px',
        display:'flex'
    },
    capture:{
        
        height:'440px',
        //backgroundColor:'green',
        borderStyle:'solid',
        borderRadius:'10px',
        borderBlockColor:''
    }
  }));
  
function Withdraw() {
    const [result, setResult] = useState('No result')
    const [values, setValues] = useState('1')
    const classes = useStyles();

    const UpdatStore = (e) => {
        Axios.put('http://localhost:3001/api/updateStoreWithdraw', {
          result: result,
          values: values,
        }).then(() => {
          console.log('withdraw')
        }).then(() => {
          setResult('Withdraw OK!')
        }).then(window.location.reload(false))

        Axios.post('http://localhost:3001/api/historyWithdraw', {
          result: result,
          values: values,
          //GID: props.Usergid,
        }).then(() => {
          console.log('History Add')
        })
    }

    function IsScann () {
      const pushResult = [''] 
      if  ( result != null && result !== undefined && result !== ''){
        pushResult.push(result)
        console.log(pushResult[1])
      }
        return (
        <div>
          <Grid item={2}>
            
          </Grid>
        </div>
      )}
     
    return (
        <Grid container className={classes.container} >
            <Paper  elevation={3} className={classes.form}>
                <Grid item xs={5} >
                <QrReader
                delay={4000}
                onError={err => console.log(err)}
                onScan={(data) => setResult(data)}
                style={{ width: '100%'}}/>
                <div styles={{display: 'flex'}}>
                    <p> {result} </p>
                </div>
                </Grid>
            </Paper>

            <Paper  elevation={3} className={classes.capQr}>
                <Grid item xs={12} >
                    <div>
                    asd
                    </div>
                </Grid>
            </Paper>
        </Grid>
    )
}

export default Withdraw
