import React, { useState } from 'react'
import axios from 'axios'
import { Form, InputGroup, FormControl } from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { Nav } from 'react-bootstrap';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
/////////////////////////////////////////////////////////////////// File
//import RoteComponentAdd from './RouteComponentAdd/RoteComponentAdd'
import AddToStore from './RouteComponentAdd/AddToStore'
import AddRegister from './RouteComponentAdd/AddRegister'

const useStyles = makeStyles((theme) => ({
    add: {
        '& > *': {
            padding: '30px',
            marginTop: '24px'},
    },
    Form: {
        marginTop: '16px'
    },
    A :{
        marginTop: '80px'
    }
}))

function Add() {
    const classes = useStyles();

    const [PartCode, setPartCode] = useState("none")
    const [Brand, setBrand] = useState("none")
    const [Location_at, setLocation] = useState("none")
    const [Description, setDescription] = useState("none")

    const handlerSubmit = (e) => {
        axios.post('http://localhost:3001/api/insert', {
            PartCode : PartCode,
            Brand : Brand,
            Location_at : Location_at,
            Description : Description,
        }).then(() => {
            alert("successfull insert")
        })
    }

    return (
        <Router>
            <div className={classes.Form}>
                <Nav justify variant="tabs" defaultActiveKey="/">
                    <Nav.Item>
                        <Nav.Link to="/AddToStore">ADD TO STORE</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link to="/AddRegister">ADD REGISTER</Nav.Link>
                    </Nav.Item>
                </Nav>   
                <Switch>
                    <Route path="/AddToStore" >   <AddToStore />  </Route>
                    <Route path="/AddRegister" >  <AddRegister /> </Route>
                </Switch>
            </div>
        </Router>
    )
}

export default Add
