import React from 'react';
import BarcodeScannerComponent from "react-webcam-barcode-scanner";
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
 
const useStyles = makeStyles((theme) => ({
    root: {
      margin: "20px",
      marginTop: '50px'
    },
    root2: {
        marginTop: '30px'
    },
    p:{
        backgroundColor: 'coral',
        height: '30px',
        width: '150px',
        textAlign: 'center'
    }
}))

function ReactWebcamBarcodeScanner() {
  const classes = useStyles();

  const [ data, setData ] = React.useState('Not Found');
 
  return (
    <div>
        <div className={classes.root2}>
            <BarcodeScannerComponent
                width={400}
                height={400}
                onUpdate={(err, result) => {
                if (result) setData(result.text)
                else setData('Not Found')
                }}
            />
        </div>
        <p className={classes.p}>{data}</p>
        <Button className={classes.root} variant="contained" color="Primary" >Add</Button>
        <Button className={classes.root} variant="contained" color="Primary" >Out</Button>
    </div>
  )
}
 
export default ReactWebcamBarcodeScanner;
