import React, { useState } from 'react';
//import BarcodeScannerComponent from "react-webcam-barcode-scanner";
//import BarcodeReader from 'react-barcode-reader'
import QrReader from 'react-qr-scanner'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Axios from 'axios'
 
const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex'
    },  
    root1: {
      textAlign: 'center'
    },
    root2: {
        
    },
    p:{
        
    }
}))

function ReactQrCodeScanner() {
  const classes = useStyles();

  const [ result, setResult ] = useState('Not Found')
 
    const UpdatStore = (e) => {
      Axios.put('http://localhost:3001/api/updateStoreAdd', {
        result:result,
      }).then(() => {
        console.log('test')
      })
    }

    const OutStore = () => {
      Axios.post('http://localhost:3001/api/updateStoreOut', {
        result:result,
      }).then(() => {
        console.log('test')
      })
    }

    const handleError = (err) => {
      console.error(err)
    }

    const handleScan = (data) => {
      setResult({
        result: data,
      })
    }

  return (
    <Grid container >
      <Grid className={classes.root} item xs={12}>
        <Grid item xs={8} ></Grid>
        <Grid item xs={4}>
          <div className={classes.root1}>
            <div style={{marginTop: '0px',marginLeft:'0px'}}>
              <QrReader
                  delay={50}
                  onError={err => console.log(err)}
                  onScan={(data) => setResult(data)}
                  style={{ width: '100%', height: '200px' }}
              />
            </div>
              <p className={classes.p}>{result}</p>
              
              <Button fullWidth variant="contained" color="Primary" onClick={UpdatStore}>Withdraw</Button>
                

          </div>
        </Grid>
      </Grid>
    </Grid>
  )
}
 
export default ReactQrCodeScanner;

