      'Text Sending' : text,
    }
    e.preventDefault();
    console.log(submitt)
    let formdata = new FormData();
    formdata.append("text",submitt);
    const url = "http://localhost:80/testcode/" ; 
    axios.post(url, formdata).then(res=> console.log(res.data).then(err => console.log(err)))


  }

  useEffect(() => {

  })


  return (
    <div className="App">
      <header className="App-header">

        <input onChange={(e) => setText(e.target.value)} type="text" id="text" placeholder="enter some text..."/>

        <button onClick={handlerSubmit} id="submit">Save</button>
      </header>
    </div>
  );
}



export default App;
