import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'
import Button from '@material-ui/core/Button';
import Axios from 'axios'
import { Form } from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    scan :{
        
    }
  }));
  
function Withdraw() {
    const [result, setResult] = useState('No result')
    const [values, setValues] = useState('1')
    const classes = useStyles();

    const UpdatStore = (e) => {
        Axios.put('http://localhost:3001/api/updateStoreWithdraw', {
          result: result,
          values: values,
        }).then(() => {
          console.log('withdraw')
        }).then(() => {
          setResult('Withdraw OK!')
        }).then(window.location.reload(false))

        Axios.post('http://localhost:3001/api/historyWithdraw', {
          result: result,
          values: values,
          //GID: props.Usergid,
        }).then(() => {
          console.log('History Add')
        })
    }

    function IsScann () {
      const pushResult = [''] 
      if  ( result != null && result !== undefined && result !== ''){
        pushResult.push(result)
        console.log(pushResult[1])
      }
        return (
        <div>
          <Grid item={2}>
            
          </Grid>
        </div>
      )}
     
    return (
        <div>
            <h1>Withdraw Scan...</h1>
            <div style={{display:'flex'}}>
                <Paper spacing={1} className={classes.scan}>
                    <Grid item xs={10}> 
                        <div style={{padding:'50px'}}>
                        <QrReader
                            delay={4000}
                            onError={err => console.log(err)}
                            onScan={(data) => setResult(data)}
                            style={{ width: '100%'}}/>
                        </div>
                        <h4 >PART CODE : {result}</h4>
                    </Grid>
                </Paper>
            </div>
        </div>
    )
}

export default Withdraw
