import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import { Grid } from '@material-ui/core';
import ReactQrCodeScanner from '../PageDashboard/ReactQrCodeScanner';
import QrReader from 'react-qr-scanner'

//import ReactScannerA from
//import ReactScannerA from

function rand() {
    return Math.round(Math.random() * 20) - 10;
  }
  
  function getModalStyle() {
    const top = 50 
    const left = 50 
  
    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: `translate(-${top}%, -${left}%)`,
    };
  }

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  rootBtn: {
    '& > *': {
      margin: theme.spacing(0.1),
    },display: 'flex'
  },
}));

export default function SModal() {
  const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [openA, setOpenA] = React.useState(false);
  const [openB, setOpenB] = React.useState(false);

  const handleOpenA = () => {
    setOpenA(true);
  };
  const handleCloseA = () => {
    setOpenA(false);
  };
  const handleOpenB = () => {
    setOpenB(true);
  };
  const handleCloseB = () => {
    setOpenB(false);
  };

  const bodyA = (
    <div  className={classes.paper}>
      <QrReader />
    </div>
  );

  const bodyB = (
    <div  className={classes.paper}>
      
    </div>
  );

  return (
    
        <Grid item xs={12}>
        <div style={modalStyle} className={classes.rootBtn}>
            <Button fullWidth variant="contained" color="secondary" onClick={handleOpenA}> 
                Withdraw 
            </Button>     
            <Button  fullWidth variant="contained" color="Primary" onClick={handleOpenB}> 
                To Store  
            </Button>
        <Modal
            open={openA}
            onClose={handleCloseA}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
        >
            {bodyA}
        </Modal>

        <Modal
            open={openB}
            onClose={handleCloseB}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
        >
            {bodyB}
        </Modal>
        </div>
      </Grid>
  );
}
