//import './App.css';
import React,{ useState } from 'react'
import {BrowserRouter as Router,} from "react-router-dom";
import Axios from 'axios'
/////////////////////////////////////////////////////// M-ui
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { Form } from 'react-bootstrap';
import Button from '@material-ui/core/Button';


/////////////////////////////////////////////////////// File
import Add from './Add/Add'
//import user from '../UserStore.json'
//import LoginPaper from './LoginPaper/LoginPaper'
import Search from './Search/Search'
import SModal from './SModal/Modal'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    margin: '10px'
  },
  Navbar: {
    padding: theme.spacing(6),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  Main: {
    padding: theme.spacing(6),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    height: 660 ,
    flexDirection: 'row',
    display: 'flex',
  },
  PaperL: {
    padding: theme.spacing(1),
    height: 350 ,
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  PaperR: {
    padding: theme.spacing(1),
    height: 350 ,
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  Button: {
    '& > *': {
      margin: theme.spacing(1),
      padding: '20px',
      position: 'relative',
      justify: 'space-evenly'
    },
  logout: {
    marginTop: '50px',
  }
  },
  rootBtn: {
    '& > *': {
      margin: theme.spacing(0.1),
    },display: 'flex'
  },
  a: {
    flexGrow: 1,
    flexDirection: 'center',
    alignContent: 'center'
  },
  b: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    margin: '20px',
    backgroundColor: '#ffffff'
  
  },
  c:{
      marginBottom: '50px'
  }
}));



function Dasboard() {
  const classes = useStyles();
  const [Usergid, setUsergid] = useState("")
  const [Userpassword, setUserpassword] = useState("")
  const [L, setL] = useState(true)

  var Login = true
  var auth = false

  const login = (e)  => {
    console.log(Userpassword)
    console.log(Usergid)

  }

  const logout = details => {
    console.log('logout')
  }
  
  return (
    <Router>
      <div className="App">
        <div className={classes.root}>
          <Grid container spacing={3}>
            <Grid item xs={3}>
            <Paper className={classes.PaperL}>
              {Login ? (
                <div className={classes.a}>
                <Grid container spacing={3}>
                    <Grid item xs={12} >
                        <Paper className={classes.b} elevation={0}>
                            <div className={classes.c}>
                                <h1>FACILITY STORE</h1>
                                <Grid item xs={12} style={{marginTop: '40px'}}>
                                    <Form >
                                        <Form.Group >
                                            <Form.Control type="text" size="md" placeholder="GID"  onChange={(e) => { setUsergid(e.target.values)}}/>
                                        </Form.Group>
                                        <Form.Group >
                                            <Form.Control type="text" size="md" placeholder="Password"  onChange={(e) => { setUserpassword(e.target.values)}}/>
                                        </Form.Group>
                                    </Form>
                                </Grid>
                                <Grid item xs={12}>
                                    <Form.Group >
                                        <Button variant="contained" fullWidth color="Primary" onClick={login}>
                                            Login
                                        </Button>
                                    </Form.Group>
                                </Grid>
                            </div>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
              ) : (<> LogOut</>)}
            </Paper>
            </Grid>
            <Grid item xs={9}>
              <Paper className={classes.PaperR}>
                <Grid item xs={12}>
                </Grid>
              </Paper>
            </Grid>
            <Grid item xs={12}>
              <Paper className={classes.Main}>
                <Grid item xs={3} spacing={3}>
                  <Add />
                  <Grid item xs={10}>
                  <div className={classes.rootBtn}>
                    {!Login? (<SModal />):(<>Please Login</>)}
                  </div>
                  </Grid>
                </Grid>
                <Grid item xs={9}>
                  <Search /> 
                </Grid>
              </Paper>
            </Grid>
          </Grid>
        </div>
      </div>
    </Router>
  );
}


export default Dasboard;


