    <Grid container >
      <Grid className={classes.root} item xs={12}>
        <Grid item xs={8} ></Grid>
        <Grid item xs={4}>
          <div className={classes.root1}>
              <div className={classes.root2}>
                  <BarcodeScannerComponent
                      width={500}
                      height={200}
                      onUpdate={(err, result) => {
                        if (result) {
                          setData(result.text)
                        } else {
                          setData('Not Found')
                        }
                    }}
                    />
              </div>
              <p className={classes.p}>{data}</p>
              
                <Button fullWidth variant="contained" color="Primary" onClick={UpdatStore}>Add</Button>
                <Button fullWidth variant="contained" color="Primary" onClick={OutStore}>Out</Button>

          </div>
        </Grid>
      </Grid>
    </Grid>
  )
}
 
export default ReactWebcamBarcodeScanner;
