import Axios from 'axios'
import React ,{ useState, useEffect, useMemo }from 'react'
import ReactTable from 'react-table'
//import { COLUMNS } from './ComponentSearch/DataColumns'

class Search extends React.Component {
    constructor(props){
        super(props);
        this.state={
            posts:[]
        }
    }

    componentDidMount() {
        const url = "'http://localhost:3001/api/get'";
    
        fetch(url, {
          method: 'GET',
        }).then(response => response.json()).then(posts => {
         this.setState({posts: posts})
        })
      }

 render(){
    const data = this.state.posts;
    const columns = [
       {
         Header: "userID",
         accessor: "userId",
         style:{
           textAlign: "right"
         },
         width: 100
       },
       {
         Header: "ID",
         accessor: "id",
         style:{
           textAlign: "right"
         },
         width: 50
       },
       {
         Header: "Title",
         accessor: "title"
       },
       {
         Header: "Content",
         accessor: "body"
       },
       {
         Header: "Actions",
         filterable: false,
         sortable: false,
         resizable: false,
         Cell: porps =>{
           return(
             <button style={{background: "red", color: "#fefefe"}}
               onClick={(e)=> {
                   this.deletePoste(porps.original.id);
               }}
             >Delete</button>
         )},
         width: 100,
         maxWidth: 100,
         minWidth: 100,
       }
     ]
     return(
         <div>
             <ReactTable columns={columns}>

             </ReactTable>
         </div>
     )
 }   
}

export default Search
