import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'
import Button from '@material-ui/core/Button';
import Axios from 'axios'
import { Form } from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    rootBtn: {
      '& > *': {
        margin: theme.spacing(0.1),
      },display: 'flex'
    },
  }));
  
function ReactScannarB() {
    const [result, setResult] = useState('No result')
    const [values, setValues] = useState('1')
    const classes = useStyles();

    const ToStore = (e) => {
        Axios.put('http://localhost:3001/api/updateToStore', {
          result: result,
          values: values,
         }).then(() => {
           console.log('to store')
         }).then(() => {
           setResult('OK!')
         //console.log(result)
         }).then(window.location.reload(false))
    }

    function IsScann () {
      const pushResult = [''] 
      if  ( result != null && result !== undefined){
        //console.log()
        //console.log(pushResult)
        //setDelay(8000)
        pushResult.push(result)
        console.log(pushResult[1])
        //console.log(result)
      }
    }
    
    return (
        <div >
            <QrReader
                delay={300}
                onError={err => console.log(err)}
                onScan={(data) => setResult(data)}
                style={{ width: '100%', height: '200px' }}/>
            <div styles={{display: 'flex'}}>

                <Grid item={12}>
                    <div className={classes.rootBtn}>
                        <Grid item={4}>
                            <Form > 
                                <Form.Group>
                                    <Form.Control type="number" size="md" placeholder="Values" onChange={(e) => setValues(e.target.value)}/>
                                </Form.Group>
                            </Form>
                        </Grid>
                        <Grid item={4}>
                            <Button  variant="contained" color="Primary" onClick={ToStore}> To Store </Button>
                        </Grid>
                        </div>
                </Grid>
                <p>MESSAGE : {result}</p>

            </div>
        </div>
    )
}

export default ReactScannarB
