import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'

function QrScan() {
    const [result, setResult] = useState('No result');

    return (
        <div>
            <QrReader
                delay={50}
                onError={handleError}
                onScan={handleScan}
                style={{ width: '100%' }}
            />
            <p>{this.state.result}</p>
        </div>
    )
}

const handleScan = () => {
    if (data) {
      this.setState({
        result: data
      })
    }
  }
const handleError = err => {
    console.error(err)
  }

export default QrScan
