import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'
import Button from '@material-ui/core/Button';
import Axios from 'axios'
import { Form } from 'react-bootstrap';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    rootBtn: {
      '& > *': {
        margin: theme.spacing(0.1),
      },display: 'flex'
    },
  }));
  
function ReactScannarA() {
    const [result, setResult] = useState('No result')
    const [values, setValues] = useState(500)
    const [keep, setKeep] = useState('')
    const [delay, setDelay] = useState(5000)
    const classes = useStyles();

    const UpdatStore = (e) => {
        Axios.put('http://localhost:3001/api/updateStoreAdd', {
          result: result,
          values: values,
        }).then(() => {
          console.log('withdraw')
        }).then(() => {
          setResult('Withdraw OK!')
          //console.log(result)
        }).then(window.location.reload(false))
    }

    function IsScann () {
      const pushResult = [''] 
      if  ( result != null && result !== undefined){
        //console.log()
        //console.log(pushResult)
        //setDelay(8000)
        pushResult.push(result)
        console.log(pushResult[1])
        //console.log(result)
      }
        return (
        <div>
          <Grid item={12}>
            <h4>PART CODE : {pushResult[1]}</h4>
            <div className={classes.rootBtn}>
                <Grid item={4}>
                    <Form > 
                        <Form.Group>
                            <Form.Control type="number" size="md" placeholder="Values" onChange={(e) => setValues(e.target.value)}/>
                        </Form.Group>
                    </Form>
                </Grid>
                <Grid item={4}>
                    <Button  variant="contained" color="Primary" onClick={UpdatStore}> Withdraw </Button>
                </Grid>
            </div>
          </Grid>
        </div>
      )}
