import React , { useState } from 'react'
import axios from 'axios'
import QrReader from 'react-qr-scanner'


function Home() {
    const [result, setResult] = useState('No result');
    ///////////////////////////////////////////////////////
    const [PartCode, setPartCode] = useState("none")
    const [Brand, setBrand] = useState("none")
    const [Location_at, setLocation] = useState("none")
    const [Description, setDescription] = useState("none")

    const handlerSubmit = (e) => {
        axios.post('http://localhost:3001/api/insert', {
            PartCode : PartCode,
            Brand : Brand,
            Location_at : Location_at,
            Description : Description,
        }).then(() => {
            alert("successfull insert")
        })
    }

    const handlerAdd = (e) => {
        axios.post('http://localhost:3001/api/insert', {   ///
            PartCode : PartCode,                           ///
        }).then(() => {
            alert("successfull To ADD")
        })
    }

    return (
        <div>
            <div className="App">
                <header className="App-header">
                    <QrReader
                        delay={100}
                        onError={err => console.log(err)}
                        onScan={(data) => setResult(data)}
                        style={{ width: '100%' }}
                    />
                    
                    <p>{result}</p>
                    <button onClick={handlerAdd} id="submit">ADD</button>
                    <br></br>
                    <input onChange={(e) => setPartCode(e.target.value)} type="text" id="text" placeholder=" Part Code..."/>
                    <br></br>
                    <input onChange={(e) => setBrand(e.target.value)} type="text" id="text" placeholder=" Brand..."/>
                    <br></br>
                    <input onChange={(e) => setLocation(e.target.value)} type="text" id="text" placeholder=" Location..."/>
                    <br></br>
                    <input onChange={(e) => setDescription(e.target.value)} type="text" id="text" placeholder=" Description..."/>
                    <br></br>
                    <button onClick={handlerSubmit} id="submit">Save</button>
                </header>
            </div>
        </div>
    )
}

export default Home
