import React , { useState } from 'react'
import QrReader from 'react-qr-scanner'

function QrScan_() {
    const [result, setResult] = useState('No result');

    handleScan = data => {
        if (data) {
          this.setState({
            result: data
          })
        }
      }
      handleError = err => {
        console.error(err)
      }

    return (
        <div>
            <QrReader
                delay={50}
                onError={this.handleError}
                onScan={this.handleScan}
                style={{ width: '100%' }}
            />
            <p>{this.state.result}</p>
        </div>
    )
}

export default QrScan_
