import React, { useState } from 'react'
import axios from 'axios'
import { Form, InputGroup, FormControl } from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { Nav } from 'react-bootstrap';

/////////////////////////////////////////////////////////////////// File
import RoteComponentAdd from './RouteComponentAdd/RoteComponentAdd'

const useStyles = makeStyles((theme) => ({
    add: {
        '& > *': {
            padding: '30px',
            marginTop: '24px'},
    },
    Form: {
        marginTop: '16px'
    },
    A :{
        marginTop: '80px'
    }
}))

function Add() {
    const classes = useStyles();

    const [PartCode, setPartCode] = useState("none")
    const [Brand, setBrand] = useState("none")
    const [Location_at, setLocation] = useState("none")
    const [Description, setDescription] = useState("none")

    const handlerSubmit = (e) => {
        axios.post('http://localhost:3001/api/insert', {
            PartCode : PartCode,
            Brand : Brand,
            Location_at : Location_at,
            Description : Description,
        }).then(() => {
            alert("successfull insert")
        })
    }

    return (
        
            <div className={classes.Form}>
                <Grid container spacing={1}>
                    <Nav justify variant="tabs" defaultActiveKey="/home">
                        <Nav.Item>
                            <Nav.Link href="/home">Active</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link eventKey="link-1">Loooonger NavLink</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link eventKey="link-2">Link</Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link eventKey="disabled" disabled>
                            Disabled
                            </Nav.Link>
                        </Nav.Item>
                    </Nav>
                    <Grid item xs={2}>
                        <RoteComponentAdd />
                    </Grid>
                    <Grid item xs={2}>

                    </Grid>
                    <Grid item xs={4} spacing={2}>
                    <div className={classes.A}>
                        <Form>
                            <Form.Group >
                                <Form.Control type="text" size="md" placeholder="Part Code" onChange={(e) => setPartCode(e.target.value)} />
                            </Form.Group>
                            <Form.Group >
                                <Form.Control type="text" size="md" placeholder="Brand" onChange={(e) => setBrand(e.target.value)}/>
                            </Form.Group>
                            <Form.Group >
                                <Form.Control type="text" size="md" placeholder="Location" onChange={(e) => setLocation(e.target.value)}/>
                            </Form.Group>
                            <InputGroup>
                                <InputGroup.Prepend>
                                    <InputGroup.Text>Description</InputGroup.Text>
                                </InputGroup.Prepend>
                                <FormControl as="textarea" size="md" aria-label="With textarea" onChange={(e) => setDescription(e.target.value)}/>
                            </InputGroup>
                            <Form.Group className={classes.add}>
                                <Button variant="contained" fullWidth color="Primary" onClick={handlerSubmit}>
                                    Add to Store
                                </Button>
                            </Form.Group>
                        </Form>
                        <Grid item xs={4}>
                        </Grid>
                    </div>
                    </Grid>
                </Grid>
        </div>
    )
}

export default Add
